
/**表1：fk_product_fname-类名列表**/
create table fk_product_fname(
	fid INT PRIMARY KEY AUTO_INCREMENT,	       #产品编号！！！！对应于family表的 family_pid项
	id varchar(16),                            #折叠id
	fname VARCHAR(64) 			                   #标题——炸粉类
);
/**表2：fk_product_family--产品列表**/
CREATE TABLE fk_product_family(
	pid INT PRIMARY KEY AUTO_INCREMENT,	       #产品编号=1-卡拉粉 2-卡拉粉D....
	family_pid INT,	      		                 #类编号=1-炸粉类 2-腌渍粉.....
	fname VARCHAR(64), 			                   #标题——炸粉类
	subname varchar(64),                       #副标题——卡拉粉
	md  VARCHAR(256),                          #中图片路径
	lg  VARCHAR(256),                          #大图片路径
	subtitle VARCHAR(128),                     #副标题下的说明——Crunchy Powder
	pstatus INT,                               #库存状态=1-供货中 0-断货中
	price INT                                  #商品价格325
);
/**表3：fk_product-spec---规格列表  **/
CREATE TABLE fk_product_spec(
	uid INT PRIMARY key AUTO_INCREMENT,         #编号  无意义
	pid INT,                                    #产品编号！！！！对应family表的pid编号项！
	pnumber VARCHAR(64),                        #商品编号——AA1019  不唯一！
	spec VARCHAR(64),	                          #规格 也不唯一，是商品编号+名称
	pnews VARCHAR(64),                          #商品资讯 也不唯一  是规格：说明  此项只要输入说明内容即可
	package VARCHAR(64)                         #包装规格		
);

